    char c[100001][11];                      //定数组，用二维数组，以数字字符串来统计 
    scanf("%d %d",&n,&m);                    //输入变量，字符型用%c，整型用%d，浮点型用%f 
    for(i=1;i<=n;i++)                        // 循环的开始
