            else k=k+y;
        }
        else
        {
            if(x==0)k=k+y;
            else k=k-y;
        }
        if(k>n)k-=n;                        //上文介绍的判断范围 
        if(k<=0)k+=n;
    }
    printf("%s",c[k]);                      //输出谁拿了爸爸的眼睛 
}
