//建立一个结构体,face表示小人的朝向，name表示小人的名字

void input() {
	cin>>n>>m;
	for(int i=1;i<=n;i++) {
		cin>>toy[i].face;cin>>toy[i].name;
	}
}

void solve() {
   bool x;//表示左数/右数
   int y;//表示数的个数
   for(int i=1;i<=n;i++) {
      if(!toy[now].face) {
         if(!x) {
            now-=y;
            if(now<=0) {
               now+=n;
               /*
               特判一下，想像1 2 3 4 5 6构成了一个环，那么从1左数应该从6倒着数。
               那么为啥要+n呢？
               因为now+n=n-(-now),而(-now)>0,所以+n就等于从n倒着数
               */
            }
         }
         if(x) {
            now+=y;
            if(now>n) {
               now-=n;
               /*
                 同上,但这次是now>n,所以我们应该-n。
                 那有的同学说为什么不是%n呢？
                 因为题目中说“1<si<n”所以si的最大值是n-1，
                 即使现在now=n，右数加上si最大是2n-1，而2n-1-n=n-1<n，所以不用%。
	            */
            }
         }
      }
      else if(toy[now].face) {
         //朝向圈外
         //朝向圈内的左数=朝向圈外的右数
         //朝向圈内的右数=朝向圈外的左数  
         if(!x) {
            now+=y;
            if(now>n) {
               now-=n;
            }
         }
         if(x) {	
            now-=y;
            if(now<=0) {
               now+=n;
            }
         }
      }
   }
}

void output() {
   cout<<ans<<endl;
}

int main() {
   input();
   solve();
   output();
   return 0;
}
