		dp[0][1][0]=n;
		dp[1][n][0]=1;//注意这是环
		for(register int i=1; i<=lg[n]; i++)//递推出倍增数组
		{
			for(register int j=1; j<=n; j++)
			{
				dp[0][j][i]=dp[0][dp[0][j][i-1]][i-1];
				dp[1][j][i]=dp[1][dp[1][j][i-1]][i-1];
			}
