{
    cin>>n>>m;
    for (int i=1;i<=n;i++)
      cin>>a[i]>>s[i];
    x=1;//坐标，表示现在已经搜到了第几个玩偶了
    for (int i=1;i<=m;i++)
    {
        cin>>t>>k;
        t=(t+a[x])%2;//上面已经解释了
        if (t==0)
        {
            x=x-k;
            if (x<1) x+=n;
        }
        else
        {
            x=x+k;
            if (x>n) x-=n;
        }
    }
    cout<<s[x];//直接输出即可
}
