    {
        cin>>towards[i]>>job[i];//逆时针读入 
    }
    for(i=0;i<m;i++)//0-1或1-0都是逆时针向下走（即++），1-1或0-0都是顺时针向下走（即--） 
    {
        cin>>direction[i]>>_distance[i];
        if(towards[now]^direction[i]==1)
