把小人和指令都看成对象，直接模拟转圈圈



```cpp
#include <iostream>
#include <vector>
#include <string>
using namespace std;
struct toy {
    bool face_outside; //为真时朝向外面
    string name; //职业（反正职业没重复的，不如看做名字好了）
    toy(bool face, string s) : face_outside(face), name(s) {}
};
struct tip {
    bool right; //为真时向右数
    int num;
    tip(bool r, int n) : right(r), num(n) {}
};
int n,m;
vector<toy> toys;
vector<tip> tips;
void load(istream &in)
{
    in>>n>>m;
    toys.push_back(toy(true,"")); //占0位
    int a,b;
    string s;
    for(int i=0;i<n;i++)
    {
        in>>a>>s;
        toys.push_back(toy((a==1?true:false),s));
    }
    for(int i=0;i<m;i++)
    {
        in>>a>>b;
        tips.push_back(tip((a==1?true:false),b%n));
    }
}
toy& solve()
{
    int p=1; //这是一个指针用途的变量，处理完一条指令后指向当前停留的小人
    for(size_t i=0;i<tips.size();i++)
    {
        if(toys[p].face_outside)
        {
            if(tips[i].right)
            {
                p-=tips[i].num; //外，右，则加
            }
            else
            {
                p+=tips[i].num; //外，左，则减
            }
        }
        else
        {
            if(tips[i].right)
            {
                p+=tips[i].num;  //内，右，则减
            }
            else
            {
                p-=tips[i].num; //内，左，则加
            }
        }
        if(p<1)
        {
            p+=n; //当p小于1，即已经转过一圈，加上n修正
        }
        if(p>n)
        {
            p-=n; //当p大于n，即已经转过一圈，减去n修正
        }
    }
    return toys[p];
}
int main()
{
    load(cin);
    cout<<solve().name<<endl;
    return 0;
}
```

