#include <cmath>
#include <cstdio>
#include <cstring>
#include <iostream>
#include <algorithm>
using namespace std;
int f[2510],s[2510];
int n,m,i,j,a;
int main(){
    scanf("%d%d",&n,&m);
    fill(f+1,f+n+1,0x7fffffff);
    //fill函数的意思是：把f[1..n]赋值为0x7fffffff(2147483647)
    for(i=1;i<=n;i++){
        scanf("%d",&a);
        if (a==1) s[i]=s[i-1]+1;
        else s[i]=s[i-1]-1;
        //统计贡献
    }
    for(i=1;i<=n;i++)
    for(j=1;j<=i;j++)
    if (abs(s[i]-s[j-1])==i-j+1||abs(s[i]-s[j-1])<=m)
    f[i]=min(f[i],f[j-1]+1);
    //动态转移方程
    printf("%d",f[n]);
    return 0;
}
```

