        int moli=0,linli=0;//初始化一下
        for(int j=i;j<=n;j++){
            if(a[j]==1) linli++;//按照题意，进行分类累加
            if(a[j]==2) moli++;
            if(linli==0 || moli==0 || abs(linli-moli)<=m) //按照题意：要么保证整个机房都是同一位神牛的膜拜者，或者两个神牛的膜拜者人数差不超过M 。           
