uses math;
var
  a,f:array[0..2501]of longint;
  n,m,i,j,x,y:longint;
begin
  readln(n,m);
  for i:=1 to n do read(a[i]);
  f[1]:=1;//边界
  for i:=2 to n do 
    begin
      f[i]:=maxlongint;
      x:=0;//表示从j到i为1的有几个
      y:=0;//表示从j到i为2的有几个
      for j:=i downto 1 do 
        begin
          case a[j] of 
            1:inc(x);
            2:inc(y);
          end;//判断第j个人属于哪个阵营
          if (abs(x-y)<=m)or(x=0)or(y=0) then f[i]:=min(f[i],f[j-1]);//从j个人到第i个人可以乘一辆车,即两种人相差不超过m或只有一种人，然后在满足条件的f[j-1]找个min
        end;
      inc(f[i]);//从j到i这些人需要一辆车
    end;
  write(f[n]);//最终答案保存在f[n]
end.
