{
    a[0]=0;                   
    b[0]=0;                            //初始化
    cin>>n;
    for(int i=1;i<=n;i++)              //这个程序最经典的部分，一个循环搞定 
    {
        cin>>a[i];                     //输入 
        if(a[i]>a[i-1]) b[i]=b[i-1]+1;      //进行比较，如果比上一个大则这个数列继续上升 
        else b[i]=1;                        //比上一个小就从头开始 
