#include<bits/stdc++.h>
using namespace std;
long long a[2000001]={0},b[2000001]={0},c[2000001]={0};
int main(){
   long long m,n,j,k,l,i,r,s;
     cin>>m>>n;
   k=1;
   for(j=0;j<m;j++){
      cin>>l>>i;
      while(i--){
         a[k]=a[k-1]+l;  //  储存第一个人每个单位时间的路程
         k++;
      }
   }
   r=1;
   for(j=0;j<n;j++){
      cin>>l>>i;
      while(i--){
         b[r]=b[r-1]+l;       ///  储存第一个人每个单位时间的路程
         r++;
      }
   }
   l=min(k,r);
   i=max(k,r);
   for(j=1;j<=l;j++){         ///第一个人在前面用1 表示
      if(a[j]>b[j]){
         c[j]=1;
      }
      else if(a[j]<b[j]){      ///第二个人在前面用 -1 表示
         c[j]=-1;
      }
      else  c[j]=c[j-1];     /// 如果等于让他等与前面的数值
   } 
   for(j=l+1;j<=i;j++){      ///有可能 两个人的时间不同 让剩下的在存到c里
      if(k>r){
          if(a[j]>b[l]){
             c[j]=1;
          }
          else if(a[j]<b[l]){
             c[j]=-1;
          }
          else  c[j]=c[j-1];
      }
      if(k<r){
         if(a[l]>b[j]){
             c[j]=1;
          }
          else if(a[l]<b[j]){
             c[j]=-1;
          }
          else  c[j]=c[j-1];
      }
   }
   s=0;
   for(j=1;j<i;j++){         ///遍历c
      if(c[j]!=c[j+1]){
         //cout<<j<<" "<<c[j]<<endl;
         s++;
      }
   }
   cout<<s<<endl;
   return 0;
}

```

