//	freopen("1.out","w",stdout);
	read(n);
	for(int i=1;i<=n;i++)
	{
		read(a[i]);
		maxx=max(maxx,a[i]);
	}
	while(maxx!=1)
	{
		ans=inf;
		num=0;
		dfs(maxx,0,1,1);
		b[++tot]=ans;
		maxx=ans-1;
	}
	for(int i=1;i<=n;i++)
		work(a[i]),
		printf("\n");
	return 0;
}
