using namespace std;

int n,m,s;

struct que{
	int x,y;
}q[10010];

int cmp1(que a,que b)
{
	if(a.x==b.x) return a.y<b.y;
	return a.x<b.x;
}

int cmp2(que a,que b)
{
	if(a.y==b.y) return a.x<b.x;
	return a.y<b.y;
}

int main()
{
