	q[++s].x=n,q[s].y=0;
	q[++s].x=0,q[s].y=m;
	q[++s].x=n,q[s].y=m;
	int ans=0;  //加上四个边界，不用在dp特判边界情况
	sort(q+1,q+s+1,cmp1);//从左到右排序
	for(i=1;i<=s;i++)
	{
		int r=m,l=0,h=n-q[i].x;
		for(j=i+1;j<=s;j++)
		{
