#include<bits/stdc++.h>    //万头文件
using namespace std;
int k,n,ans=0,i=1;
int lowbit(int x)          //自定义
{
	int y=0;
	while(x)
	{
		x=x-((x)&(-x));     //x&（-x）是求0的个数，所以x-((x)&(-x))是求1的个数
		y++;
	}
	return y;               //x为2进制是1的个数
}
int main()
{
	scanf("%d%d",&n,&k);
	while(lowbit(n)>k）
	{
		ans+=((n)&(-n));
		n+=((n)&(-n));
	}
	printf("%d",ans);
}
```
