巨丑的完整代码：
```cpp
#include <bits/stdc++.h>

using namespace std;

typedef long long LL;

LL n,limit,num;

deque<LL>q;

int main(){
    cin>>n>>limit;

    while(n){
        if(n&1) q.push_front(num++);
        else num++;//num记录是2的几次方
        n>>=1;
    }

    num=0;//下面num为当前新增瓶数

    while((int)q.size()>limit){
        q.push_back(q[(int)q.size()-1]);
        num+=pow(2,q[(int)q.size()-1]);
        while(q[(int)q.size()-1]==q[(int)q.size()-2]){
            q[(int)q.size()-2]++;
            q.erase(q.end()-1);
            if((int)q.size()<=limit) {
                cout<<num;
                return 0;
            }
        }
    }
    cout<<num;//不加这个只能得80分
    return 0;
}


```
