int n, k, a, b, ans, o = 1;
priority_queue <int, vector <int>, greater <int> > q;   //优先队列，越小的整数优先级越高的队列 
int main ()
{
	scanf ("%d %d", &n, &k);
	while (n > 0){
		a = n % 2;
		if (a == 1)
			q.push (o);
		o = o * 2; 	
		n = n / 2;
	}//将能合并的水合在一起 
	while (q.size () > k){ //判断队列的元素个数是否小于等于k 
		a = q.top (); 
		q.pop ();
		b = q.top ();
		q.pop ();
		//取出最小的两个元素 
		if (a == b)
			q.push (a + b);//如果相同则合并 
		else{
			q.push (b);
			ans = ans + a;
			q.push (a * 2);//不相同则买a个瓶子合并 
		}
	}	
	printf ("%d", ans);//输出 
	return 0;
}

