        read(b[i]);
    for i:=1 to n do 
       begin c[i]:=i; read(a[i]);
 end;    qsort(1,n);
     for i:=1 to n do a[i]:=a[i]+b[(i-1) mod 10+1];  qsort(1,n); 
    for i:=1 to k do
    write(c[i],' ');
end.



