#include<iostream>
using namespace std;
int a[20001][2],n,k,e[11];
void qsort(int ,int ,int [][2]);
void swap(int &,int &);
int main()
{
	cin>>n>>k;
	for(int i=1;i<=10;++i)
		cin>>e[i];
	for(int i=1;i<=n;++i)
	{
		a[i][0]=i;
		cin>>a[i][1];
	}
	qsort(1,n,a);  
	for(int i=1;i<=n;++i)
		a[i][1]+=e[(i-1)%10+1];
	qsort(1,n,a);
	for(int i=1;i<=k;++i)
		cout<<a[i][0]<<' ';
	return 0;
}
void qsort(int l,int r,int a[][2])
{
	int i=l,j=r,mid,mid_n;
	mid=a[(l+r)/2][1];
	mid_n=a[(l+r)/2][0];
	while(i<=j)
	{
		while(a[i][1]>mid||(a[i][1]==mid&&a[i][0]<mid_n))
		i++;
		while(a[j][1]<mid||(a[j][1]==mid&&a[j][0]>mid_n))
		j--;
		if(i<=j)
		{
			swap(a[i][1],a[j][1]);
			swap(a[i][0],a[j][0]);
			i++;
			j--;
		} 
	} 
	if(l<j) qsort(l,j,a);
	if(i<r) qsort(i,r,a);
}
void swap(int &a,int &b)
{
	int t=a;
	a=b;
	b=t;
	return ;
}
