    for(int i=0;i<n;i++){
        p[i].d=i+1;
    }
    for(int i=0;i<n;i++){
        p[i].c=(p[i].d-1)%10+1;
        p[i].w+=e[p[i].c];
    }
    sort(p,p+n,cmd);
    for(int i=0;i<k;i++){
        cout<<p[i].z<<' ';
    }
    return 0;
}
