        l[i]=l[i-1]+x;
    }
    for(int i=1;i<=n;i++) {
        int x;
        scanf("%d",&x);
        m[i]=m[i-1]+x;
    }
    for(int i=1;i<=n;i++){
        for(int j=1;j<=i;j++){
            if(l[i]-l[j-1]>=minn&&l[i]-l[j-1]<=maxn) f[i][j]=max(f[i][j],max(f[i][j-1],f[i-1][j-1]+m[i]-m[j-1]));
            else f[i][j]=max(f[i-1][j],f[i][j-1]);
        }
    }
    cout<<f[n][n];
}
