#include<bits/stdc++.h>
using namespace std;
long long f[15];//f[i]表示一个i位数有多少合法解
long long ten[11]//ten[i]表示10^i;
int len(unsigned int x)//求一个整数的位数
{
	int s=0;
	while  (x>0)
		{
			s++;
			x/=10;
		}
	return s;
}
void work()
{
	unsigned int n;
	cin>>n;
	int l=len(n);
	long long ans=0;	
	for  (int i=0;i<l;i++)
		{
			int x=n/ten[i]%10;//提取第i+1小的位
			if  (x>6)  x--;//对于大于等于7的数需要舍去7
			ans+=f[i+1]*x;//因为第i小位是合法解(1<=i<=x&&i!=7)
            //i==0的情况会由更低位先处理
			//cout<<i<<' '<<x<<endl;		
		}
	cout<<ans<<endl;	
}
int main()
{
	int t;
	cin>>t;
	ten[0]=1;
	for  (int i=1;i<=10;i++)
		ten[i]=ten[i-1]*10;
	f[1]=1;	
	for  (int i=2;i<=11;i++)	
		f[i]=f[i-1]*9;//只有带7的不合法，10-1=9，所以*9
	while (t)
		{
			work();
			t--;
		}
}
