#include<cstdio>
#include<cctype>
using namespace std;
int read(){
    int abs=0;char c=getchar();//abs储存9进制数，c读入字符 
    bool b=1;//储存是否出现了7 
    while( !isdigit(c) ) c=getchar();//判断是否为数字字符，防炸 
    while( isdigit(c) ) {
        abs*=9;//9进制进位处理 
        if(b&&c=='7') {//第一次读到7 
            b=0;//标记 
            abs+=6; 
            c=getchar();
            continue;
        }
        if(b) abs+=(c<'7')?(c-'0'):(c-'1');//未读到7，把数加上去（数为8、9时加7、8） 
        else abs+=8;//读到过7，直接加9进制的9，即8 
        c=getchar();
    }
    return abs;//将读到的数直接转化为9进制输出 
}
int main(){
