#include<bits/stdc++.h>
#define Endl endl
using namespace std;
int a[10000005];
int t,n,m,sum=0;
void mul(int a[],int k)//乘法部分
{
	for(int i=1;i<a[0];i++)//乘法
	a[i]*=k;
	for(int i=1;i<a[0];i++)//进位，这样进位是为了让程序更有可读性
	{
		if(a[i]>9)
		{
			a[i+1]+=a[i]/10;
			a[i]%=10;
		}
	}
	while(a[a[0]])//在最高位不为零时接着进位
	{
		a[a[0]+1]+=a[a[0]]/10;
		a[a[0]]%=10;
		a[0]++;
	}
}
void check()//查询数码
{
	for(int i=1;i<a[0];i++)
	{
		if(a[i]==m)
		sum++;
	}
}
int main ()
{
	scanf("%d",&t);
	while(t--)
	{
		memset(a,0,sizeof(a));//记得每组输入之前初始化
		sum=0;
		a[0]=1,a[1]=1;
		cin>>n>>m;
		for(int i=1;i<=n;i++)
		mul(a,i);
		check();
