#include<bits/stdc++.h>
#define ll long long
#define it set<int>::iterator
using namespace std;
set<int> mp;
vector<int> p,g;
int pri[20];
int cnt;
inline ll ksm(ll x,ll p,ll md)
{
    ll ans=1;
    while(p)
    {
        if(p&1) ans=(__int128)ans*x%md;
        x=(__int128)x*x%md;
        p>>=1;
    }
    return ans;
}
inline ll ksmn(ll x,ll p)
{
    ll ans=1;
    while(p)
    {
        if(p&1) ans=ans*x;
        x=x*x;
        p>>=1;
    }
    return ans;
}
inline bool test(ll n,ll a,ll d)
{
    if(n==2||n==a) return 1;
    while(!(d&1)) d>>=1;
    ll t=ksm(a,d,n);
    while(d!=n-1&&t!=1&&t!=n-1)
    {
        t=(__int128)t*t%n;
        d<<=1;
    }
    return t==n-1||d&1==1;
}
inline bool MR(ll n)
{
    if(n==2||n==3) return 1;
    if(n==1||n%6!=1&&n%6!=5) return 0;
    if(!test(n,11,n-1)||!test(n,61,n-1))
        return 0;
    return 1;
}
inline ll f(ll x,ll t,ll n)
{
    return ((__int128)x*x+t)%n;
}
inline ll fj(ll x)
{
    ll l,r,t,s;
    for(;;)
    {
        t=rand()%(x-1)+1;
        l=f(0,t,x);
        r=f(l,t,x);
        while(l!=r)
        {
            s=__gcd(abs(l-r),x);
            if(s>1&&s<x) return s;
            l=f(l,t,x);
            r=f(f(r,t,x),t,x);
        }
    }
}
inline void PR(ll x)
{
    if(x==4)
    {
        mp.insert(2);
        return;
    }
    if(x==1) return;
    if(MR(x)) 
    {
        mp.insert(x);
        return; 
    }
    ll qwq=fj(x);
    while(x%qwq==0) 
        x/=qwq;
    PR(qwq),PR(x);
} 
void dfs(int d,int mul,int fl)
{
	if(d==cnt) return;
	dfs(d+1,mul,fl);
	dfs(d+1,mul*pri[d],-fl);
	if(fl==1) g.push_back(mul*pri[d]);
	else p.push_back(mul*pri[d]);
}
int check(int j)
{
	int s=0,i;
	for(i=0;i<p.size();i++)
		s+=j/p[i];
	for(i=0;i<g.size();i++)
		s-=j/g[i];
	return s;
}
