#include <iostream>
#include <cstdio>
using namespace std;

typedef long long ll;

ll gcd(ll x,ll y){
    if(x==0) return y;
    return gcd(y%x,x);
}

int main(){
    ll n,k,phi,t,ans,cnt=0;
    cin>>n>>k;
    t=phi=n;
    for(ll i=2;i*i<=n;i++){
        if(t%i==0){
            phi=phi-phi/i;
            while(t%i==0){
                t=t/i;
            }
        }
    }
    if(t>1){
        phi=phi-phi/t;
    }
    for(ll i=1;i<=n;i++){
        if(gcd(i,n)==1){
            cnt++;
            if(cnt==k%phi){
                ans=i;
                break;
            }
        }
    }
    cout<<(k/phi)*n+ans<<endl;
    return 0;
}
```

