#include<bits/stdc++.h>
using namespace std;
int n,a[50];
unsigned long long ans;
bool stop()//判断是否循环完了，因为阶乘会炸，但55分的代码不会 
{
	for(int i=1;i<=n;i++)
		if(a[i]!=n-i+1)
			return true ;
	return false ;
}
bool judge()//判断是否满足错排条件 
{
	for(int i=1;i<=n;i++)
		if(a[i]==i)//不满足条件返回false; 
			return false;
	return true;
}
int main()
{
	scanf("%d",&n);
	if(n==2||n==0)
	{
		printf("1\n");
		return 0;
	}
	if(n==1)
	{
		printf("0\n");
		return 0;
	}
	//写两个特判 
	for(int i=1;i<=n;i++)
		a[i]=i;//给数组赋初值 
	while(stop())
	{
		next_permutation(a+1,a+n+1);
		if(judge())
			ans++;
	}//快乐地循环 
	printf("%lld\n",ans);//输出答案 
	return 0;
}

```

