讲述了三种做法，第三种和第四种本质都是容斥
PS：自然指数做法会有精度误差过不了最后两个点
```cpp
#include<bits/stdc++.h>
#define For(i, j, k) for(register int i = (j); i <= (k); ++i)
using namespace std;

typedef long long ll;
const int N = 20 + 10;
const double e = 2.71828182845904523536;
int n;
ll D[N], fac[N];

inline void Init(int n){
	fac[0] = fac[1] = 1;
	For(i, 2, n) fac[i] = fac[i - 1] * i;
}

namespace DT{
	inline void Solve(){
		D[0] = 0, D[1] = 0, D[2] = 1;
		For(i, 3, n) D[i] = 1ll * (i - 1) * (D[i - 1] + D[i - 2]);
		printf("%lld\n", D[n]);
	}
}

namespace FAC{
	inline void Solve(){
		ll ans = 0;
		For(i, 2, n){
			if(!(i & 1)) ans += fac[n] / fac[i];
			else ans -= fac[n] / fac[i];
		}
		printf("%lld\n", ans);
	}
}

namespace RC{

	ll C(int n, int m) { return fac[n] / fac[n - m] / fac[m];}

	inline void Solve(){
		ll ans = fac[n];
		For(i, 1, n){
			if(i & 1) ans -= C(n, i) * fac[n - i];
			else ans += C(n, i) * fac[n - i];
		}
		printf("%lld\n", ans);
	}
}

namespace EEE{
	inline void Solve(){
		ll ans = (ll) (1.0 * fac[n] / e + 0.5);
		printf("%lld\n", ans);
	}
}

int main(){
