        	if(s[i+3]=='c')
			c=c;
			else
            if(s[i+3]=='a')
            c=a;
            else
            if(s[i+3]=='b')
            c=b;
            else
            c=s[i+3]-'0';
        }
        i+=5;//直接跳到下一个字母
    }
    cout<<a<<" "<<b<<" "<<c;
