```
#include<iostream>
#include<string.h>
#include<algorithm>
using namespace std;

string s;
string reverse_head(string s) {
    int zeroCount = 0;
    reverse(s.begin(), s.end());

    for(auto i:s) {
        if(i == '0') { zeroCount++; }
        else break;
    }
    s.erase(s.begin(), s.begin() + zeroCount);
    return (s == "") ? "0" : s;
}

string reverse_tail(string s) {
    int zeroCount = 0;
    reverse(s.begin(), s.end());
    int cnt = (int)s.size();
    for(int i = cnt-1; i >= 0; i--) {
        if(s[i] == '0') zeroCount++;
        else break;
    }
    s.erase(s.end() - zeroCount, s.end());
    return (s == "") ? "0" : s;
}

int main() {
    getline(cin, s);
    int cnt = (int)s.size();
    string temp1, temp2;
    if(s[cnt - 1] == '%') {
        temp1 = s.substr(0, cnt - 1);
        cout << reverse_head(temp1) << "%" << endl;
        return 0;
    } else if(s.find('/') != string::npos) {
        int pos1 = (int)s.find('/');
        temp1 = s.substr(0, pos1);
        temp2 = s.substr(pos1 + 1);
        temp1 = reverse_head(temp1);
        temp2 = reverse_tail(temp2);
        cout << temp1 << "/" << temp2 << endl;
        return 0;
    } else if(s.find('.') != string::npos) {
        int pos2 = (int)s.find('.');
        temp1 = s.substr(0, pos2);
        temp2 = s.substr(pos2 + 1);
        temp1 = reverse_head(temp1);
        temp2 = reverse_tail(temp2);
        cout << temp1 << "." << temp2 << endl;
        return 0;
    }
    cout << reverse_head(s) << endl;

    return 0;
}

```

