    };
    int dat[6],dunc[7]={1,2,1,1,2,3};//对于unc字符组准备一个索引表，取出的时候方便
    char temp[20];
    int index(char *g){
        for(int i=0;i<20;i++)//检查在不在dict里面
            for(int j=0;g[j]!='\0';j++){
