void quicksort(int left,int right)//快排走起
{
	if (left>right)//判断是否相遇
		return;
	int i,j,t;
	t=a[left];//t中存基准书
	i=left;
	j=right;
	while(i!=j)
	{
		while(a[j]>=t&&i<j)
			--j;//顺序很重要，要先从右往左找
		while(a[i]<=t&&i<j)
			++i;//顺序很重要，再从左往右找
		if (i<j)//当左右相遇，交换
			swap(a[i],a[j]);
	}
	a[left]=a[i];
	a[i]=t;//基准书归位
	quicksort(left,i-1);//继续处理左边的，这里是一个递归的过程
	quicksort(i+1,right);//继续处理右边的，这里是一个递归的过程
	return;//快排结束
}
