void dfs(int k,long long sum)
{
    int i;
    if(k>t)
    {
        if(sum<minn)minn=sum;
        return;
    }
    for(i=1;i<=t;i++)
    if(b[i]==0)
    {
        b[i]=1;
        dfs(k+1,sum*100+a[i]);
        b[i]=0;
    }
}
