        //从原进制转换为10进制
        String s1 = new BigInteger(t1,b).toString(10);
        String s2 = new BigInteger(t2,b).toString(10);
        //两个十进制的大数相加
        BigInteger x,y;
        x = new BigInteger(s1);
        y = new BigInteger(s2);
        x = x.add(y);
        //将相加结果转换为原进制
        String ans=new BigInteger(x.toString(),10).toString(b);
        System.out.println(ans.toUpperCase());
    }
}

