```
#include <bits/stdc++.h>
using namespace std;

int k;
const int N=2000+10;
char s1[N],s2[N];
int a[N],b[N],res[N+N];

int main()
{
	scanf("%d",&k);
	scanf("%s",s1);
	scanf("%s",s2);
	int l1=strlen(s1);
	int l2=strlen(s2);

	for(int i=0;i<l1;++i)
	{
		if(s1[i]>=48 && s1[i]<=57)
		{
			a[l1-i] = s1[i]-48;
		}
		else if(s1[i]>=65  && s1[i]<=90)
		{
			a[l1-i] = s1[i]-55;
		}
	}

	for(int i=0;i<l2;++i)
	{
		if(s2[i]>=48 && s2[i]<=57)
		{
			b[l2-i] = s2[i]-48;
		}
		else if(s2[i]>=65  && s2[i]<=90)
		{
			b[l2-i] = s2[i]-55;
		}
	}

	for(int i=1;i<=l1||i<=l2;++i)
	{
		res[i]+=(a[i]+b[i]);
		if(res[i]/k)
		{
			res[i+1]+= res[i]/k;
			res[i]%=k;
		}
	}

	int p=l1+l2;
	while(res[p]==0 && p!= 1)
	{
		--p;
