#### 下面请看完整代码与讲解：
```cpp
#include<cstdio>
using namespace std;
int n,m,tot,sx,sy,fx,fy,t,t1,t2;//根据题意设好变量，不用过多的解释了哦
int f[10][10];//路线数组
bool b[10][10];//标记数组

void dfs(int a,int b)

{
	if(a==fx&&b==fy)//达到终点的标志

	{
		tot++;
		return ;
	}
	else 
	{
		f[a][b]=0;//起始坐标设为0

		if(f[a-1][b]!=0) { dfs(a-1,b); f[a-1][b]=1;}

		if(f[a][b-1]!=0) { dfs(a,b-1); f[a][b-1]=1; }
		if(f[a+1][b]!=0) { dfs(a+1,b); f[a+1][b]=1;}

		if(f[a][b+1]!=0) { dfs(a,b+1); f[a][b+1]=1;}//向上下左右四个方向模拟（应该很好理解吧）

	}
}
int main()
{
	scanf("%d%d%d",&n,&m,&t);

	scanf("%d%d%d%d",&sx,&sy,&fx,&fy);

	for(int i=1;i<=n;i++)
		for(int j=1;j<=m;j++)

			f[i][j]=1;//首先把所有的位置设为1，意味着都没有障碍

	for(int i=1;i<=t;i++)
	{
		scanf("%d%d",&t1,&t2);

		f[t1][t2]=0;
	}//把有障碍的坐标设为0
	dfs(sx,sy);
    printf("%d",tot);
	return 0;
}

