```cpp

#include <cstdio>
int n, m, sx, sy, fx, fy, ans;
char map[10][10];        //地图 0为可走 1为不可走
int mx[4] = {0,1,0,-1};    //移动
int my[4] = {1,0,-1,0};

void dfs(int x, int y)
{
    if(x>n || y>n || x<1 || y<1)    //检查越界
        return;
    if(map[x][y])                    //检查是否能走
        return;
    if(x == fx && y==fy)            //是否到达终点
    {//要先检查能走 再检查是否到达终点 终点有可能是墙壁
        ans++;
        return;
    }

    map[x][y] = 1;        //标记已经走过 不能走了
    for(int i=0; i<4; i++)    //四种移动方式
        dfs(x+mx[i], y+my[i]);    
    map[x][y] = 0;        //取消标记
}

int main()
{
    int t, tx, ty;
    scanf("%d%d%d", &n, &m, &t);
    scanf("%d%d%d%d", &sx, &sy, &fx, &fy);
    while(t--)
    {
        scanf("%d%d", &tx, &ty);
        map[tx][ty] = 1;
    }
    if(!map[fx][fy])    //如果终点是墙 直接输出0
        dfs(sx, sy);
    printf("%d", ans);
    return 0;
}

