    if (a[i][j-1]==1)//判断是不是障碍
    {
        a[i][j-1]=0;
        go(i,j-1);//向下方节点寻找可以走的下一步 
        a[i][j-1]=1;
    }
    if (a[i][j+1]==1)//判断是不是障碍
    {
        a[i][j+1]=0;
        go(i,j+1);//向下方节点寻找可以走的下一步 
        a[i][j+1]=1;
    }
}
