#include <iostream>
using namespace std;
char c[6][6];
int cnt;
int dir[4][2] = {
    {1,0},
    {0,1},
    {-1,0},
    {0,-1}
};//下一步
int n,m;
bool vis[6][6];//记录是否走过
bool in(int sx, int sy){//是否在地图内
    return sx >= 0 && sx < n && sy >= 0 && sy < m;
}
void dfs(int sx, int sy){//深搜
    vis[sx][sy] = true;
    if(c[sx][sy] == 'T'){
        cnt++;
        return;
    }//如果是终点，计数++，返回
    for(int i=0; i<4; i++){
        int tx = sx + dir[i][0];
        int ty = sy + dir[i][1];//记录下一步的坐标
        if(in(tx,ty) && !vis[tx][ty] && c[tx][ty] != '#'){//判断递归条件
            vis[tx][ty] = true;//标记
            dfs(tx,ty);//递归搜索
            vis[tx][ty] = false;//尝试回溯
        }
    }
    return;
}   
