```cpp
#include<bits/stdc++.h>     //万能头就很舒服
using namespace std;
int n,ans=0,m,t,sx,sy,fx,fy;
int s[10][10];     //记录这个点有没有走过
int z[4][2]={{1,0},{0,1},{-1,0},{0,-1}};     //走的方向
void dfs(int x,int y)
{
	if(x==fx&&y==fy)
	{
		ans++;        //判定到终点了没有
	}
	for(int i=0;i<4;i++)
	{
		int xx,yy;     //不能定全局变量，不然会出事
		xx=x+z[i][0];
		yy=y+z[i][1];
		if(s[xx][yy]==0)    //0代表这个点没有走过
		{
			s[xx][yy]=1;      //搜索，最基础的步骤
			dfs(xx,yy);
			s[xx][yy]=0;      //回溯，这步不能少
		}
	}
}
int main()
{
	int a,b;
	cin>>n>>m>>t;
	cin>>sx>>sy>>fx>>fy;
	for(int i=0;i<=n+1;i++)
	{
		for(int j=0;j<=m+1;j++)
		{
			if(i==0||j==0||i==n+1||j==m+1)
			{
				s[i][j]=1;     //初始化迷宫
			}
		}
	}
	for(int i=1;i<=t;i++)
	{
		cin>>a>>b;
		s[a][b]=1;      //将障碍放入迷宫
	}
	s[sx][sy]=1;     //标记起点
	dfs(sx,sy);
	cout<<ans;
}
```

