	for(int i = 0;i < 4;i++){
		dfs(x + zx[i],y + zy[i]);//四个方向的递归
	}
	t[x][y]=0;
}

int main(){
	scanf("%d %d %d %d %d %d %d",&a,&b,&c,&sx,&sy,&fx,&fy);
	for(int i = 1;i <= c;i++){
		int bx,by;
		scanf("%d%d",&bx,&by);
		t[bx][by]=1;
	}
	dfs(sx,sy);
	cout << ans;
	return 0;
}
