AC代码如下。


```cpp
#include<cstdio>
#include<cstring> 
using namespace std;
bool m1[10][10];//避免重复走  
int ax[4]={1,-1,0,0},ay[4]={0,0,1,-1};//四个方向 
int sx,sy,fx,fy,ans=0,xx,yy,n,m;//起点x，起点y，终点x，终点y 
void dfs(int x,int y)
{
    if(x==fx&&y==fy)//如果到终点了，答案++，返回 
    {
        ans++;
        return ;
    }
    for(int j=0;j<4;j++)
    {
        int xx=x+ax[j];//这里一定一定注意要int，否则只有四十分 
        int yy=y+ay[j];
        if(xx>=1&&xx<=n&&yy>=1&&yy<=m&&m1[xx][yy]==false)
        {
            m1[xx][yy]=true;
            dfs(xx,yy);
            m1[xx][yy]=false;//回溯置标记 
        }
    }
    return;//如果搜不到答案，返回 
}
int main()
{
    int t,zx,zy;
    memset(m1,false,sizeof(m1));
    scanf("%d%d%d",&n,&m,&t);
    scanf("%d%d%d%d",&sx,&sy,&fx,&fy);
    m1[sx][sy]=true;//起点置标记 
    for(int i=0;i<t;i++)
    {
        scanf("%d%d",&zx,&zy);
        m1[zx][zy]=true;//障碍标记 
        if(zx==fx&&zy==fy)//如果终点处有障碍，直接不可能达到； 
        {
            printf("0");
            return 0;
        }
    } 
    dfs(sx,sy);
    printf("%d",ans); 
    return 0;
}
