    cin >> sx >> sy >> fx >> fy;
    for(int i=1; i<=t; i++) {
        cin >> x >> y;
        a[x][y] = 1; //标记这个点有路障 
    }
    dfs(sx, sy); //从点(sx,sy)开始深搜 
    cout << tot << endl;
    return 0;
}
