如果数字很大，超过四千万，那么

```puts("Prime? No!\nThe number is too large!\n");```

否则判断质数，若不是质数则分解质因数。具体实现请参考程序以及注释

```cpp
#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
string s;
int cnt, p[50010], c[50010];
#define w() puts("Enter the number=")
inline bool is_prime(int n) {
	if (n < 2) return 0;
	for (register int i = 2; i <= sqrt(n); ++i)
		if (n % i == 0) return 0;
	return 1;
}
void divide(int n) {
	if (is_prime(n)) {
		puts("Prime? Yes!\n");
		return;
	}//判定是否为质数 
	if (n < 2) {
		puts("Prime? No!\n");
		return;//特判<2 
	}
	cnt = 0;
	int tmp = n;
	for (register int i = 2; i <= sqrt(n); ++i) {
		if (n % i == 0) {
			p[++cnt] = i, c[cnt] = 0;
			while (n % i == 0) n /= i, ++c[cnt];
		}
	}
	if (n > 1) p[++cnt] = n, c[cnt] = 1;

	puts("Prime? No!");
	printf("%d=", tmp);
	for (register int i = 1; i < cnt; ++i)
		printf("%d^%d*", p[i], c[i]);
	printf("%d^%d", p[cnt], c[cnt]);
	puts("\n");
}//分解质因数，基本功 
int main() {
	//cout << (-7) % 3;
	//freopen("data.out", "w", stdout);
	//刚开始忘把文件去掉，导致WA,大家要注意！！！ 
	puts("Enter the number=");
	while (getline(cin, s)) {
		ll res = 0;
		bool flag1 = 0, flag2 = 0;
		//flag1表示数字是否过大，flag2表示字符串中有无数字 
		for (register int i = 0; i < s.size(); ++i) {
			if (!isdigit(s[i])) continue;//不是数字，跳过 
			flag2 = 1;//有数字 
			res = (res << 3) + (res << 1) + s[i] - 48;
			if (res >= 40000000) flag1 = 1;//数字很大 
		}
		//cout << res << endl;
		if (!flag2) {
			break;//字符串中没有数字，那么结束循环 
		}
		if (flag1) {
			puts("Prime? No!\nThe number is too large!\n");
			w();//数字过大了，输出并跳过 
			continue;
		}
		divide(res);//分解质因数 
		w();
	}
}
