			while(k*p<=6325)
			{
				pim[k*p]=0;
				k++;
			}
			pr[++cnt]=p;
		}
	}
   /*筛法求质数,不会去看模板p3383*/
	while(read());//直到返回0时(就是输入的都是符号,结束)
	return 0;
}
