#include <bits/stdc++.h>
#define N 1001
#define INF 0x7f7f7f7f
using namespace std;

int n, m;
int a[N];
int f[N][N];

inline int read()
{
    char ch = getchar();
	int x = 0, f = 1;
    while(ch > '9' || ch < '0')
	{
		if(ch == '-')
			f = -1;
		ch = getchar();
	}
    while(ch >= '0' && ch <= '9')
	{
		x = x * 10 + ch - '0';
		ch = getchar();
	}
    return x * f;
}

inline int Min(int a, int b)
{
	return a > b ? b : a;
}

void Solve()
{
	sort(a + 1, a + m + 1);
	a[0] = 0, a[m + 1] = n + 1;
