using namespace std;
#define ll long long
#define mod 10000 
int f[10001];
ll k(ll a,ll b,ll c)//快速幂算法
{
    ll ans=1;
    while (b)
    {
        if (b%2==1)
            ans=(ans*a)%c;
        a=(a*a)%c;
        b/=2;
    }
