int x[4]={-2,-1,1,2},y[4]={1,2,2,1},n,m,total=0;
void DSF(int h,int l)
{
	if(h==n&&l==m)
	{
		total++;
		return;
	}	//累加方案数并返回
	for(int i=0;i<4;++i)
	{
		int u=x[i]+h,v=y[i]+l;
		if(u<=n&&u>=0&&v<=m&&v>=0&&a[u][v]==0)	//判断
		{
			a[u][v]=1;
			DSF(u,v);	//回溯
			a[u][v]=0;
		} 
	}
}
int main()
{
	cin>>n>>m;
	a[0][0]=1;	//将起始坐标赋值为1
	DSF(0,0);	//开始深搜
	cout<<total;
	return 0;
}
```
