    while a[j]>mid do dec(j);
    if i<=j then
     begin
       t:=a[i];a[i]:=a[j];a[j]:=t;
       inc(i);dec(j);
     end;
  until i>j;
  if l<j then qsort(l,j,a);
  if i<r then qsort(i,r,a);
